Context()->flags->set( reduceConstants => 0, reduceConstantFunctions => 0,   );
$answer=Formula("(3+sqrt(13))/2,(3-sqrt(13))/2");
$my_evaluator = $answer -> cmp(
 showHints=>1,
 showLengthHints => 1,
 partialCredit => 1,
);

$a = Compute("(4+sqrt(12))/2")->with(
  tolType => 'absolute',
  tolerance => .0051,
);

$b = Compute("(4-sqrt(12))/2")->with(
  tolType => 'absolute',
  tolerance => .0051,
);
$answer = List("$a,$b");
$my_evaluator = $answer -> cmp(
 showHints=>1,
 showLengthHints => 1,
 partialCredit => 1,
  tolType => 'absolute',
  tolerance => .0051,
);

