$yint = 3;
$zeros = List("-2,2,4");
$my_evaluator = $zeros -> cmp(
 showHints=>1,
 showLengthHints => 1,
 partialCredit => 1,
); 
$min = -3;
$tmin = -4;
$intervals = new_checkbox_multiple_choice();
$intervals -> qa (
"A question can go here, but PTX ignores this",
"from \( -5 \) to \( -4 \)$BR",
"from \( 1 \) to \( 3 \)$BR",
);
$intervals -> extra(
"from \( 2 \) to \( 4 \)$BR",
"from \( -5 \) to \( -2 \)$BR",
);
$intervals -> makeLast( "from \( -5 \) to \( -4 \)$BR",
"from \( -5 \) to \( -2 \)$BR",
"from \( 2 \) to \( 4 \)$BR",
"from \( 1 \) to \( 3 \)$BR",
"None of the above");

$functions = new_checkbox_multiple_choice();
$functions -> qa (
"A question can go here, but PTX ignores this",
"(b)$BR",
);
$functions -> extra(
"(a)$BR",
"(c)$BR",
);
$functions -> makeLast(
"(a)$BR",
"(b)$BR",
"(c)$BR",
"None of the above");
