Context()->variables->add(V=>"Real",w=>"Real");
$m = 1.75;
$f1 = Formula("V");
$f2 = Formula("$m*w");
$w = 15;
$V = $f2->eval(w=>$w);

$multians = MultiAnswer($f1,$f2)->with(
  singleResult => 1,
  checker => sub {
      my ( $correct, $student, $self ) = @_;
      my ( $f1stu, $f2stu ) = @{$student};
      my ( $f1, $f2 ) = @{$correct};
      return ( ($f1 == $f1stu && $f2 == $f2stu) ||
           ($f1 == $f2stu && $f2 == $f1stu)  
      );
  }
);

Context("Fraction")->variables->add(V=>"Real",r=>"Real");
$f1 = Formula("V");
$f2 = Formula("4/3 * pi * r^3");

$multians = MultiAnswer($f1,$f2)->with(
  singleResult => 1,
  checker => sub {
      my ( $correct, $student, $self ) = @_;
      my ( $f1stu, $f2stu ) = @{$student};
      my ( $f1, $f2 ) = @{$correct};
      return ( ($f1 == $f1stu && $f2 == $f2stu) ||
           ($f1 == $f2stu && $f2 == $f1stu)  
      );
  }
);

$YesNo =  RadioButtons(
        ["Yes, \(\dfrac{B}{r^3}\) is constant.",
        "No, \(B\) is not equal to \(r^3\)."],
        0
    );

