    Context()->variables->add(C=>"Real",p=>"Real");
    $f1 = Formula("C");
    $f2 = Formula("120/p");

    $multians = MultiAnswer($f1,$f2)->with(
      singleResult => 1,
      checker => sub {
          my ( $correct, $student, $self ) = @_;
          my ( $f1stu, $f2stu ) = @{$student};
          my ( $f1, $f2 ) = @{$correct};
          return ( ($f1 == $f1stu && $f2 == $f2stu) ||
               ($f1 == $f2stu && $f2 == $f1stu)  
          );
      }
    );

