$lead = PopUp(
    [ "?", 
    "discriminant", 
    "lead coefficient"
    ],
    2
);
$degree = PopUp(
    [ "?", 
    "degree", 
    "logarithm"
    ],
    1
);
$powers = PopUp(
    [ "?", 
    "ascending powers", 
    "descending powers"
    ],
    2
);
$six = Compute("6");

            Context("LimitedPolynomial")->variables->are(n=>"Real", p=>"Real");
            $fac1 = Compute("(5*n-p)");
            $fac2 = Compute("(25n**2+ 5*n*p  + p**2)");

$multians = MultiAnswer($fac1,$fac2)->with(
   singleResult => 0,
   allowBlankAnswers => 0,

  checker => sub {
    my $correct = shift; my $student = shift; my $ansHash = shift;
    my ($F,$G) = @{$correct};
    my ($f,$g) = @{$student};

    $ansHash->setMessage(1,"Neither factor can be constant") 
    unless $f->isFormula;
    $ansHash->setMessage(2,"Neither factor can be constant") 
    unless $g->isFormula;

    if ( (($F == $f) && ($G == $g)) ||
         (($G == $f) && ($F == $g))
       ) 
    { return [1,1];
    }elsif( ($F == $f) || ($G == $f) ) 
     {return [1,0];
    }elsif( ($F == $g) || ($G == $g) ) 
     {return [0,1];}
     else 
       {return [0,0];}    
  }
);
