webwork.pg-code:
Context()->variables->set(
  x=>{limits=>[-4-sqrt(13),-4+sqrt(13)]},
  y=>{limits=>[-3-sqrt(13),-3+sqrt(13)]},
);
$rsq = 13;
$r = sqrt($rsq);
$h = -4;
$k = -3;
$answer = ImplicitEquation(
  "(x-$h)^2  + (y-$k)^2 =$rsq",
  solutions=>[ 
     [$h,$k+$r],
     [$h,$k-$r],
     [$h+$r,$k],
     [$h-$r,$k],
  ]
);
