      singleResult => 1,
      checker => sub {
          my ( $correct, $student, $self ) = @_;
          my ( $f1stu, $f2stu ) = @{$student};
          my ( $f1, $f2 ) = @{$correct};
          return ( ($f1 == $f1stu && $f2 == $f2stu) ||
