\draw[samples=2,domain={-5:5},smooth,variable=\x,,green,thick] plot ( {\x}, { \k-\b/\a * (\x - \h) } );
\draw[samples=65,domain={-1.6:1.6},smooth,variable=\x,red,very thick, <->,>=stealth'] plot ( {\h+\a*cosh(\x)}, {\k+\b*sinh(\x)});
