$multians = MultiAnswer($l1,$l2)->with(
  singleResult => 0,
  checkTypes => 0,
  checker => sub {
      my ( $correct, $student, $self ) = @_;
      my ( $f1stu, $f2stu ) = @{$student};
      my ( $f1, $f2 ) = @{$correct};
      if ( ($f1 == $f1stu && $f2 == $f2stu) ||
           ($f1 == $f2stu && $f2 == $f1stu)  
      ) {
      return[1,1];
      } elsif ( ($f1 == $f1stu )  ||
               ($f2 == $f1stu )  
          ) {
          return [1,0];
      } elsif ( ($f1 == $f2stu)   ||
               ($f2 == $f2stu )  
          ) {
          return [0,1];
    } else {
        return [0,0];
    }
    }
);

